mapper.update:

UPDATE posts
SET title  = #{title},
    content=#{content}
WHERE id = #{id}


UPDATE posts
SET status ='F'
WHERE id = #{param1}

mapper.select:

SELECT IFNULL(MAX(step), (SELECT step FROM posts WHERE id = #{param1})) + 1
FROM posts
WHERE parent_id = #{param1};

mapper.update:

UPDATE posts
SET step = step + 1
WHERE ref_id = #{param1}
  AND step >= #{param3}
